sections.css:
@import 'helpers/classes';
@import 'base/skeleton';
@import 'base/tables';
@import 'base/forms';

@import 'components/typography/headings';
@import 'components/typography/lists';


// ========================================================================= \\

h1,
h2,
h3,
h4,
h5,
h6 {
    letter-spacing: -.005em;
    margin: .25em 0 .45em;
    padding: 0;
}

// scss-lint:disable SingleLinePerSelector
h1, .h1 { font-size: $heading--h1; }
h2, .h2 { font-size: $heading--h2; }
h3, .h3 { font-size: $heading--h3; }
h4, .h4 { font-size: $heading--h4; }
h5, .h5 { font-size: $heading--h5; }
h6, .h6 { font-size: $heading--h6; }
// scss-lint:enable SingleLinePerSelector

@import 'foundation-sites/scss/util/util';
@import 'foundation-sites/scss/global';
@import 'foundation-sites/scss/components/visibility';

$grid-row-width:     $grid--max-width;
$grid-column-count:  $grid--columns;
$grid-column-gutter: $grid--gutter;
$breakpoints: (
    'small':   map-get($grid--breakpoints, 'small'),
    'medium':  map-get($grid--breakpoints, 'medium'),
    'large':   map-get($grid--breakpoints, 'large'),
    'xlarge':  map-get($grid--breakpoints, 'xlarge'),
    'xxlarge': map-get($grid--breakpoints, 'xxlarge')
);
$breakpoint-classes: $grid--classes;

@import 'foundation-sites/scss/grid/grid';

@if ($grid--flexbox) {
    @include foundation-flex-grid;
}
@else {
    @include foundation-grid;
}

@include foundation-visibility-classes;


// ========================================================================= \\

.table {
    background-color: $table--background;
    margin-bottom: 1rem;
    max-width: 100%;
    width: 100%;

    th {
        text-align: left;
    }

    // Cells
    > thead,
    > tbody,
    > tfoot {
        > tr {
            > th,
            > td {
                border-top: 1px solid $table--border-color;
                line-height: $line-height--base;
                padding: $table--cellpadding;
                vertical-align: top;
            }
        }
    }

    // Bottom align for column headings
    > thead > tr > th {
        border-bottom: 2px solid $table--border-color;
        vertical-align: bottom;
    }

    // Remove top border from thead by default
    > caption + thead,
    > colgroup + thead,
    > thead:first-child {
        > tr:first-child {
            > th,
            > td {
                border-top: 0;
            }
        }
    }

    // Account for multiple tbody instances
    > tbody + tbody {
        border-top: 2px solid $table--border-color;
    }
}


.table--striped {
    > tbody > tr:nth-of-type(2n+1) {
        background-color: $table--stripe;
    }
}


.table--hover {
    > tbody {
        > tr:hover {
            background-color: $table--hover;
        }
    }
}


.table--bordered {
    border: 1px solid $table--border-color;

    > thead,
    > tbody,
    > tfoot {
        > tr {
            > th,
            > td {
                border: 1px solid $table--border-color;
            }
        }
    }

    > thead > tr {
        > th,
        > td {
            border-bottom-width: 2px;
        }
    }
}

fieldset {
    margin-bottom: 1rem;
}

label {
    cursor: pointer;
    display: inline-block;
    font-weight: bold;
    margin-bottom: .2em;
    max-width: 100%;
}

legend {
    font-style: italic;
    padding: 0 .75em;
}

input {
    &[type="file"] {
        display: block;
    }

    &[type="search"] {
        box-sizing: border-box;
    }

    &[type="file"]:focus,
    &[type="checkbox"]:focus,
    &[type="radio"]:focus {
        outline: thin dotted;
        outline-offset: -2px;
    }
}

textarea {
    max-width: 100%;
}



.#{$component-name}__group {
    margin-bottom: 1rem;
}


.#{$component-name}__control {
    background-image: none;
    border: 1px solid $form__control-border;
    border-radius: 3px;
    box-shadow: inset 1px 1px 3px rgba(#000, .075);
    display: block;
    padding: .45em .65em;
    // For ease of reading.
    // scss-lint:disable SpaceAfterPropertyColon
    transition:
        border-color 250ms ease-in-out,
        box-shadow 250ms ease-in-out;
    // scss-lint:enable SpaceAfterPropertyColon
    width: 100%;

    &:focus {
        // For ease of reading
        // scss-lint:disable SpaceAfterPropertyColon
        box-shadow:
            inset 0 1px 1px rgba(#000, .075),
            0 0 3px rgba($form--aura, .35);
        // scss-lint:enable SpaceAfterPropertyColon
    }

    &:not(input):not(select):not(textarea) {
        border: 0;
        box-shadow: none;
    }

    label > & {
        margin-top: .25em;
    }
}


.#{$component-name}__message {
    background: #fff;
    border: 1px solid;
    border-radius: 3px;
    color: $form__message-color;
    display: block;
    font-size: .85rem;
    // font-weight: bold;
    margin-top: 1em;
    padding: .25em;
    position: relative;

    &::after,
    &::before {
        border: solid transparent;
        bottom: 100%;
        content: ' ';
        height: 0;
        left: 1rem;
        pointer-events: none;
        position: absolute;
        width: 0;
    }

    &::after {
        border-bottom-color: #fff;
        border-width: .8em;
        margin-left:  0;
    }

    &::before {
        border-bottom-color: inherit;
        border-width: .9em;
        margin-left:  -.1em;
    }
}


.#{$component-name}__radio,
.#{$component-name}__checkbox {
    font-weight: normal;
    padding-left: 20px;
    position: relative;

    // We only want to target radio/checkbox input
    // scss-lint:disable QualifyingElement
    input[type="radio"],
    input[type="checkbox"] {
        left: 0;
        position: absolute;
        top: .25em;
    }
    // scss-lint:enable QualifyingElement

    & + & {
        margin-left: 1rem;
    }
}


// CONTEXTUAL \\
// ========== \\
.#{$component-name} {
    .is-valid {
        &.#{$component-name}__group,
        &.#{$component-name}__message,
        &.#{$component-name}__radio,
        &.#{$component-name}__checkbox,
        .#{$component-name}__group,
        .#{$component-name}__message,
        .#{$component-name}__radio,
        .#{$component-name}__checkbox {
            color: $form--success;
        }

        &.#{$component-name}__control,
        .#{$component-name}__control {
            border-color: $form--success;
            box-shadow: none;
        }
    }

    .is-invalid {
        &.#{$component-name}__group,
        &.#{$component-name}__message,
        &.#{$component-name}__radio,
        &.#{$component-name}__checkbox,
        .#{$component-name}__group,
        .#{$component-name}__message,
        .#{$component-name}__radio,
        .#{$component-name}__checkbox {
            color: $form--error;
        }

        &.#{$component-name}__control,
        .#{$component-name}__control {
            border-color: $form--error;
            box-shadow: none;
        }
    }
}


// DISABLED STATE \\
// ============== \\
// We need to be a bit specific with the elements we target for [disabled]
// scss-lint:disable QualifyingElement
.#{$component-name}__control[disabled],
fieldset[disabled] .#{$component-name}__control {
    cursor: not-allowed;
}
// scss-lint:enable QualifyingElement


// MEDIA QUERIES \\
// ============= \\
@include breakpoint('medium') {
    .#{$component-name}--inline {
        .#{$component-name}__group {
            display: inline-block;
            margin-bottom: 0;
            vertical-align: middle;
        }

        .#{$component-name}__control {
            display: inline-block;
            vertical-align: middle;
            width: auto;
        }

        .#{$component-name}__checkbox {
            display: inline-block;
        }
    }
}

@import 'components/breadcrumbs';
@import 'components/buttons';
@import 'components/channelnav';
@import 'components/flash'; // undocumented
@import 'components/gem';
@import 'components/globalnav';
@import 'components/icons';
@import 'components/logo';
@import 'components/masthead';
@import 'components/panels';
@import 'components/pip';
@import 'components/spinner';
@import 'components/switch';


// ========================================================================= \\

/**
    [1.1] - Breadcrumbs are always inline, that's just how they roll.
    [1.2] - We let users choose their own "leading" text with the data-before=""
            attribute. If it doesn't exist, the text doesn't show.
    [1.3] - The standard 1rem font-size is a bit large for breadcrumbs, so
            let's shrink it a little.
    [1.4] - This is the divider between each of the breadcrumb items. Having
            it as an ::after pseudo-element cuts down on superfluous markup.
    [1.5] - Let's do the spacing based on the font-size of the breadcrumb items.
*/
.#{$component-name} {
    @include list--inline;          // [1.1]

    &::before {
        content: attr(data-before); // [1.2]
        font-size: .85rem;          // [1.3]
        margin-right: .25rem;
    }

    > .#{$component-name}__item {
        font-size: .85rem;          // [1.3]

        > a {
            color: get-color('brand', 'primary');
            font-weight: bold;
        }

        &:not(:last-child) {
            // Is ok to nest this deep for psuedo elements
            // scss-lint:disable NestingDepth
            &::after {
                content: '\00BB';   // [1.4]
                margin: 0 .35em;    // [1.5]
            }
            // scss-lint:enable NestingDepth
        }
    }
}

@each $name, $color in $breadcrumb--loop {

    .#{$component-name}--#{$name} {

        > .#{$component-name}__item a {
            color: $color;
        }

    }

}

/**
    Standard button styles
        [1.1] - We'll remove the border from any button element, most notable is
                when the class is applied to an *actual* <button> tag.
        [1.2] - Buttons should appear clickable regardless of whether they're a
                click element (<a>), that's kinda the point.
        [1.3] - Make buttons inline-block so we can properly manipulate their
                box-model.
        [1.4] - In order to try meet UX guideline minimum heights in a reasonable
                fashion, we set our vertical padding as a calculation of our base
                font-size (assumed 14px). This is how we calculate the padding:
                ( (desiredHeight - fontSize) ÷ 2 ) ÷ fontSize
        [1.5] - Prevent the User from being able to select text in the button.
        [1.6] - Better alignment for buttons with differing sizes when used
                beside each other.
        [1.7] - Stop text in buttons breaking on to a newline halfway through.
        [1.8] - The disabled button shouldn't show any visual feedback when it's
                pressed. Otherwise, it wouldn't really be disabled.
*/
.#{$component-name} {
    background-color: $btn--primary-bg;
    border: 0;              // [1.1]
    border-radius: $btn--border-radius;
    box-shadow: $btn--box-shadow;
    color: $btn--primary-text;
    cursor: pointer;        // [1.2]
    display: inline-block;  // [1.3]
    font-size: 1rem;        // [1.4]
    font-weight: bold;
    line-height: 1;         // [1.4]
    margin: $btn--margin;
    padding: .785em 1em;   // [1.4]
    text-align: center;
    user-select: none;      // [1.5]
    vertical-align: middle; // [1.6]
    white-space: nowrap;    // [1.7]

    @media only screen and (min-resolution: 2dppx) {
        border-radius: $btn--border-radius_2x;
        box-shadow: $btn--box-shadow_2x;
    }

    @media only screen and (min-resolution: 3dppx) {
        border-radius: $btn--border-radius_3x;
        box-shadow: $btn--box-shadow_3x;
    }

    &:focus,
    &:hover {
        color: $btn--primary-text;
        text-decoration: none;
    }

    &:active {
        &:not(.btn--disabled) { // [1.8]
            background-image: linear-gradient(to bottom, $btn--pressed-overlay 0%, $btn--pressed-overlay 100%);
            box-shadow: none;
            color: rgba(#000, .2);
            text-decoration: none;
        }
    }

    &:focus {
        outline: 1px dotted #000;
    }
}

.#{$component-name}--secondary {
    background-color: design-token('color', 'brand-blue-dark');
}

.#{$component-name}--tertiary {
    background-color: design-token('color', 'brand-grey');
}

.#{$component-name}--disabled {
    background-color: $btn--disabled-bg;
    color: $btn--disabled-text;
    cursor: not-allowed;

    &:hover {
        color: $btn--disabled-text;
    }

    &:active {
        pointer-events: none;
    }
}

[class*="#{$component-name}--odds"] {
    min-width: 3.5em;
}

.#{$component-name}--odds {
    background-color: $btn--odds-bg;
    color: $btn--odds-text;
    padding-left: 0;
    padding-right: 0;

    &:hover {
        color: $btn--odds-text;
    }
}

.#{$component-name}--odds-short,
.#{$component-name}--odds-up {
    background-color: $btn--odds-short-bg;
    color: $btn--odds-short-text;
}

.#{$component-name}--odds-drift,
.#{$component-name}--odds-down {
    background-color: $btn--odds-drift-bg;
    color: $btn--odds-drift-text;
}

/**
    Channel modifiers
*/
@each $name, $color in $button--loop {
    .#{$component-name}--#{$name} {
        background-color: $color;
    }

    .#{$component-name}--#{$name}-alt {
        background-color: #fff;
        color: $color;

        &:hover {
            color: $color;
        }
    }

    .#{$component-name}--#{$name}-outline {
        background-color: transparent;
        border: 1px solid;
        box-shadow: inset 0 -2px 0 #fff;

        &:active {
            border-color: transparent;
            color: #fff;
        }
    }
}

.#{$component-name}--positive {
    background-color: get-color('brand', 'positive');
}

.#{$component-name}--negative {
    background-color: get-color('brand', 'negative');
}

/**
    [1.1] - We're able to use em units here because the base .btn elements use
            100% font-size of their parent elements for scalability.
*/
.#{$component-name}--small {
    font-size: .8em; // [1.1]
    padding: .5em;   // [1.1]
}

.#{$component-name}--large {
    font-size: 1.5em;   // [1.1]
    padding: .5em 1em; // [1.1]
}

.#{$component-name}--block {
    padding-left: 0;
    padding-right: 0;
    width: 100%;
}

/**
    [1.1] - This is to targt any buttons that are placed in the Channelnav.
            There should really only be one button in there, though. The
            "Open An Account" button.
*/
.#{$component-name} {
    background: linear-gradient(to bottom, $channelnav-gradient--top, $channelnav-gradient--bottom);
    border-bottom: 3px solid $channelnav--default;
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#{$channelnav-gradient--top}', endColorstr='#{$channelnav-gradient--bottom}', GradientType=0 );
    font-size: $font-size--small;
    line-height: 2.5;

    .#{$component-name}__item.active a {
        color: $channelnav--default;
    }

    // [1.1]
    [class*="btn"] {
        font-weight: bold;
        margin-right: 1em;
        text-transform: uppercase;
        vertical-align: baseline;
    }
}


/**
    The Channelnav Trigger is an element that is only visible on medium-sized
    screens and down. It's responsible for displaying the Channelnav List on
    smaller screens via hover and focus.
        [2.1] - This is the CSS responsible for displaying the Channlenav List.
*/
.#{$component-name}__trigger {
    color: #fff;
    float: right;
    margin-right: 1em;

    &:hover,
    &:focus {
        color: #fff;
        + .#{$component-name}__list { // [2.1]
            display: block;           // [2.1]
        }
    }
}


// The Channel Time element is exactly that. A small element that displays the
// time in the Channel Nav
.#{$component-name}__time {
    color: #fff;
    font-size: .75rem;
}


/**
    [3.1] - We don't want a background color on the Channelnav List because it
            will appear above the page on small screens. Instead, we'll set
            gradient on the individual items later on, and then further change
            them using media queries.
    [3.2] - Because these are mobile-first styles, the Channelnav List is hidden
            by default, and will display when the appropriate conditions are met
            on the Channelnav Trigger in [2.1].
*/
.#{$component-name}__list {
    @include list--unstyled;
    @include z-index(menu);
    background-color: transparent; // [3.1]
    display: none;                 // [3.2]
    margin: 0;
    min-width: 50%;
    padding-top: 3em;
    position: absolute;
    right: 0;
    top: 0;

    &:hover {
        display: block;
    }

    // A small, invisible shim so that we're always "hovering" over the
    // Channelnav List at the top of the screen.
    &::after {
        border: solid transparent;
        // These need to be in this order for the "arrow" to display
        // scss-lint:disable PropertySortOrder
        border-color: rgba($channelnav-gradient--top, 0);
        border-bottom-color: $channelnav-gradient--top;
        // scss-lint:ensable PropertySortOrder
        border-width: 10px;
        content: ' ';
        height: 0;
        margin-top: -19px;
        pointer-events: none;
        position: absolute;
        right: 2.5em;
        top: 3em;
        width: 0;
    }
}


.#{$component-name}__item {
    background: linear-gradient(to bottom, $channelnav-gradient--top, $channelnav-gradient--bottom);
    border-top: 1px solid rgba(#fff, .35);
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#{$channelnav-gradient--top}', endColorstr='#{$channelnav-gradient--bottom}', GradientType=0 );

    a {
        color: #fff;
        display: block;
        font-weight: bold;
        padding-left: .8em;
        padding-right: .8em;
        text-decoration: none;

        &:hover {
            text-decoration: underline;
        }
    }
}

@each $name, $color in $channelnav--loop {

    .#{$component-name}--#{$name} {
        border-bottom-color: $color;

        .#{$component-name}__item.active a {
            color: $color;
        }
    }

}

/**
    [1.1] - We want Gems to be inline, but also want to manipulate their box
            model.
    [1.2] - Gems should be 55% font size of their direct parent, otherwise they
            might look too small in larger elements.
    [1.3] - Gems are angry! Not really, but they should always be in upper case,
            best not to depend on the content being uppercase, though.
    [1.4] - Prevent the user from copying the content of Gems. They're not
            actually "content", but more attention grabbers than anything.
    [1.5] - Get better vertical alignment on Gems when used in other elements.
*/

.#{$component-name} {
    background-color: $gem--background;
    border-radius: 2px;
    color: $gem--color;
    display: inline-block;     // [1.1]
    font-size: .75em;          // [1.2]
    font-weight: bold;
    line-height: 1;
    padding: .25em .35em;
    text-transform: uppercase; // [1.3]
    user-select: none;         // [1.4]
    vertical-align: middle;    // [1.5]
}

@each $name, $color in $gem--loop {
    .#{$component-name}--#{$name} {
        background-color: $color;
    }
}


.#{$component-name}--priced {
    background-color: #fff;
    border: 2px solid;
    border-radius: 4px;
    color: get-color('brand', 'secondary');
}

.#{$component-name}--old {
    background-color: $gem--old-background;
    border: 1px solid;         // [1.1]
    color: $gem--old-color;
    font-size: .55rem;         // [1.3]
    padding: .25em;
}

/**
    [1.1] - Prevent the user from getting the "select text" cursor
    [1.2] - Allow icons to inherit their font-size. We add this rule in here
            because we've already used the shorthand for the rest, and want
            to overwrite.
    [1.3] - IE10 and lower is a bit of a spa, and will fail the shorthand `font`
            declaration when we use `rem`, causing it to completely ignore our
            custom icon font, so we need to define each of the rules separately.
*/
.#{$component-name} {
    cursor: default;                       // [1.1]
    display: inline-block;
    font-family: $icons--font-family-name; // [1.3]
    font-feature-settings: 'liga';
    font-size: inherit;                    // [1.2]
    -moz-osx-font-smoothing: grayscale;
    -webkit-font-smoothing: antialiased;
    font-style: normal;                    // [1.3]
    font-weight: normal;                   // [1.3]
    line-height: 1;                        // [1.3]
    text-rendering: auto;
    text-transform: none;

    &::before {
        content: map-get($icons, 'pp');
    }
}

.#{$component-name} {
    // Base Icon Names
    @each $modifier, $ligature in $icons {
        &.#{$component-name}--#{$modifier}::before {
            content: $ligature;
        }
    }

    // Aliases and Sport IDs
    @each $modifier, $ligature in $icons-sportid {
        &.#{$component-name}--#{$modifier}::before {
            content: $ligature;
        }
    }
}

/**
    [1.1] - This lets us use actual content in the Logo elements, and thus,
            have better SEO and semantics. Screen readers will read the
            element as normal, but regular users will only see the Logo.
            Brill.
*/
.logo {
    @include hide-text;  // [1.1]
    background: $logos;
    border: 0;
    display: inline-block;
    vertical-align: middle;
}

// We'll loop through each of the sprites in the logos sprite-map() and use the
// values to create BEM-ified classes with the awesomely sexy BEM-sprite mixin.
@each $i in sprite_names($logos) {
    @include BEM-sprite(logo, $logos, $i);
}

.#{$component-name} {
    @include background--stipe($masthead-background, $masthead-stripe-size, $masthead-stripe-darken, false);
    min-height: 80px;
    padding: 10px;
}


.#{$component-name} {
    .logo {
        float: right;
    }
}

.#{$component-name} {
    background-color: $globalnav-background;
    border-bottom: $globalnav-border;
    border-top: $globalnav-border;
    position: relative;
}


// The Trigger element is only shown on small and medium sized screens. Its
// purpose is to show the Globalnav List when it's hovered over.
.#{$component-name}__trigger {
    color: $globalnav__link-color;
    display: inline-block;
    font-weight: bold;
    padding: .35em;

    &:focus + .#{$component-name}__list,
    &:hover + .#{$component-name}__list {
        display: block;
    }
}


/**
    [1.1] - We'll also use padding-top in conjunction with a transparent
            background to create a small, invisible "shim" that will give the
            list a little extra hover space on top, to make mouse movement
            from the trigger to the list a little better.
    [1.2] - By default, the list is hidden, and we trigger its display when a
            user hovers over the trigger element above.
    [1.3] - We want to "push" the left and right of the list in as much as the
            gutter size in the grid. This gives the element a little breathing
            room from the edges of small devices.
    [1.4] - We also want the list to stay displayed while a user is hovering
            over it. Otherwise it would disappear when they leave the trigger.
    [1.5] - If the Globalnav List is (most likely) a child of a Foundation
            grid column, then let's negate the column padding so it sits flush
            with the sides.
*/
.#{$component-name}__list {
    @include list--unstyled;
    @include z-index(menu);
    background-color: transparent; // [1.1]
    display: none;                 // [1.2]
    left:  $grid--gutter;          // [1.3]
    margin-top: -1em;
    padding-top: 2em;              // [1.1]
    position: absolute;            // [1.3]
    right: $grid--gutter;          // [1.3]
    top:   100%;                   // [1.3]

    &:hover {
        display: block;            // [1.4]
    }

    // Our small little arrow that will appear above the list on small and medium
    // devices.
    &::after {
        border: solid transparent;
        // These properties need to be specified in this order so that the bottom
        // border retains its colour so we can see the chevron.
        // scss-lint:disable PropertySortOrder
        border-color: rgba($globalnav-background, 0);
        border-bottom-color: $globalnav-background;
        // scss-lint:enable PropertySortOrder
        border-width: 10px;
        content: ' ';
        height: 0;
        left: 1.5em;
        margin-top: -19px;
        pointer-events: none;
        position: absolute;
        top: 2em;
        width: 0;
    }

    .row .columns > & {
        margin-left:  -($grid--gutter / 2); // [1.5]
        margin-right: -($grid--gutter / 2); // [1.5]
    }
}


/**
    [2.1] - We set the Globalnav's background color on the Items instead.
            This allows us to use our transparent shim technique in [1.4].
*/
.#{$component-name}__item {
    background-color: $globalnav-background; // [2.1]
    border-bottom: $globalnav-border;

    &:last-child {
        border-bottom: 0;
    }
}


.#{$component-name}__link {
    color: $globalnav__link-color;
    display: block;
    font-size: .8rem;
    font-weight: bold;
    line-height: 2.5rem;
    padding: 0 .5rem;
    text-decoration: none;

    &:hover {
        text-decoration: underline;
    }
}


/**
    [3.1] - Having the list take up the full width of a medium-sized screen
            would be a bit too much. So we make it take up 50% instead.
*/
@include breakpoint('medium') {
    .#{$component-name}__list {
        min-width: 50%; // [3.1]
        right: auto;
    }
}


/**
    [4.1] - On large screens, we don't want the trigger to exist as the list
            will be displayed in all its glory.
    [4.2] - FLEXBOX, BABY!
    [4.3] - We don't want the List to be position absolute any more, as it
            will display in full on large screens. We also want to reset any
            positions we set above.
    [4.4] - We'll also remove the padding we set to create the small shim that
            we put in place in [1.4].
    [4.5] - Remove the arrow we created above.
    [4.6] - We also need to epxlicitly set display:flex; when a user hovers on
            the list element, otherwise it will apply the display:block; that
            we set in [1.3].
    [4.7] - Because all the List Items have a right border by default and no
            left border, the very first right-aligned looks a bit strange.
            To target this, we'll apply a left border to any elements that are
            an immediate sibling of a globalnav item that DOES NOT have a .right
            class, and it itself DOES have a .right class. This will only target
            the first .right item in our list.
    [4.8] - This is our hocus pocus that lets us use Flexbox, and also right-align
            elements within the flex container.
    [4.9] - Here, we set the margin-left:auto; to kick in the flexbox spacing for
            the right-aligned elements, and then we remove the margin-right so
            the elements fall as far right as possible.
            Next, we'll remove the margin-left from siblings so that they will
            all clump together. This leaves is with the first .right element
            only having margin-left:auto;
*/
@include breakpoint('large') {
    .#{$component-name}__trigger {
        display: none; // [4.1]
    }


    .#{$component-name}__list {
        align-content: flex-start;   // [4.2]
        align-items: flex-start;     // [4.2]
        background-color: transparent;
        display: flex;               // [4.2]
        flex-flow: row nowrap;       // [4.2]
        height: auto;
        justify-content: flex-start; // [4.2]
        left: auto;                  // [4.3]
        margin-top: 0;               // [4.4]
        padding-top: 0;              // [4.4]
        position: relative;          // [4.3]
        right: auto;                 // [4.3]
        top: auto;                   // [4.3]

        &::after {
            display: none;           // [4.5]
        }

        &:hover {
            display: flex;           // [4.6]
        }
    }


    .#{$component-name}__item {
        align-self: auto;
        border-bottom: 0;
        border-right: $globalnav-border;
        flex: 0 0 auto;

        &:first-child {
            border-left: $globalnav-border;
        }

        &:not(.right) + &.right {           // [4.7]
            border-left: $globalnav-border; // [4.7]
        }

        // [4.8]
        &.right {
            margin-left:  auto; // [4.9]
            margin-right: 0;    // [4.9]

            & + & {
                margin-left: 0; // [4.9]
            }
        }
    }
}

.#{$component-name}--mobile {
    display: flex;
    font-size: 1.35em;
    min-height: 0;
    padding: .5em;

    .btn--back {
        background-color: transparent;
        box-shadow: none;
        font-size: inherit;
        margin: auto 0;
        padding: .25em .35em;
    }

    .#{$component-name}__title {
        color: #fff;
        margin: auto;
        text-align: center;
    }
}

.#{$component-name} {
    display: block;
    margin: 0;
    margin-bottom: 1rem;
}


/**
    [1.1] - Force-remove margin, in case a <h[1-6]> tag is used
*/
.#{$component-name}__title {
    @include background--stipe($panel__title-background, 40px 40px, 5%, true);
    border-radius: $panel--border-radius $panel--border-radius 0 0;
    border-top: 1px solid $panel__title-background;
    box-shadow: inset 0 1px 0 rgba(#fff, .25);
    color: $panel__title-color;
    line-height: 2;
    margin: 0; // [1.1]
    overflow: hidden;
    padding-left: 1rem;
    padding-right: 2rem;
    position: relative;
    text-shadow: 1px 1px 0 rgba(#000, .25);
}


/**
    [2.1] - Remove the top border, as we'll have the panel__title sitting on top.
    [2.2] - The Collapse plugin will apply style="height: n;" (where n is px value)
            to the element, but doesn't animate the value. This allows us to use
            CSS animation instead!
    [2.3] - We apply overflow: hidden; to any panel__body elements that can collapse.
            This is so all of its contents "clip" and hide as the element is collapsing.
    [2.4] - If a panel__body can collapse, and is NOT in its open state, then we can
            go ahead and hide it completely.
*/
.#{$component-name}__body {
    background-color: #fff;
    border: 1px solid $panel--border-color;   // [2.1]
    border-radius: 0 0 $panel--border-radius $panel--border-radius;
    transition: height 250ms;                 // [2.2]

    &.is-collapsing {
        overflow: hidden; // [2.3]
    }

    &.can-collapse {
        &:not(.is-open) {
            display: none; // [2.4]
        }
    }
}


/**
    [3.1] - If .panel__content isn't the :last-child, we must assume a
            .panel__footer exists in the .panel element. If so, we need to
            square off the bottom of the element and remove the bottom border.
    [3.2] - We'll make the evil assumption that .panel__content will contain
            <p> elements and, if so, strip their margin and padding.
*/
.#{$component-name}__content {
    padding: 1rem;

    &:not(:last-child) {
        border-bottom: 0; // [3.1]
        border-radius: 0; // [3.1]
    }

    p {
        margin: 0;        // [3.2]
        padding: 0;       // [3.2]
    }
}

/**
    [4.1] - .panel__header and .panel__footer elements should, typically, be
            darker than the background-color of the .panel element, so we use
            rgba black to let the background-color bleed through and darken.
*/
.#{$component-name}__header,
.#{$component-name}__footer {
    background-color: rgba(#000, .1); // [4.1]
}

.#{$component-name}__header {
    border-bottom: 1px solid $panel--border-color;
    font-size: .8rem;
    line-height: 2.5;
    padding-left: 1rem;
    padding-right: 1rem;
}

.#{$component-name}__footer {
    border-top: 1px solid #ccc;
    padding: 1rem;
}

/**
    [5.1] - Make the element "clickable" to the user by displaying the correct
            cursor, regardless of whether an <a> tag is used or not.
    [5.2] - This value should match whatever the padding-left value of the
            .panel__title is in order to offset it and place the element to
            the left-most of the .panel__title.
    [5.3] - The value of these should be half of whatever the line-height value
            of .panel__title is (and in em). This is so we have even spacing
            all around the collapse element regardless of font-size.
    [5.4] - Set a value for the rotate so that the CSS has a reference value
            to animate to/from.
    [5.5] - We don't want to mess around with changing classes and showing/hiding
            elements. So instead, we'll just take the chevron icons (assuming they're
            the actual icons being used) and rotate them so that they face the
            correct direction. We'll use the details from [5.4] to animate them!
*/
.#{$component-name}__collapse {
    background-color: rgba(#000, .35);
    border-right: 1px solid rgba(#fff, .15);
    color: get-color('sports', 'primary');
    cursor: pointer;      // [5.1]
    display: inline-block;
    float: left;
    margin-left: -1rem;   // [5.2]
    margin-right: .5em;
    padding-left: .5em;   // [5.3]
    padding-right: .5em;  // [5.3]

    .icon {
        cursor: pointer;
        transform: rotate(0deg);     // [5.4]
        transition: transform 250ms; // [5.4]
    }

    &.is-collapsed {
        .icon--chevron-right {
            transform: rotate(90deg);  // [5.5]
        }

        .icon--chevron-down {
            transform: rotate(-90deg); // [5.5]
        }
    }
}

.#{$component-name}--flat {
    .#{$component-name}__title {
        background-color: $panel__title-background;
        background-image: none;
        border-top: 0;
        box-shadow: none;
    }

    .#{$component-name}__collapse {
        background-color: transparent;
        border-right: 0;
        color: #fff;
    }
}

@each $name, $color in $pip--loop {
    .#{$component-name}--#{$name} {
        background-color: $color;
    }
}

.#{$component-name} {
    color: $spinner--color;
    display: inline-block;
    font-size: 1.5rem;
    height: 1em;
    width: 1em;
}

.spinner__svg {
    animation: spinner__rotate 2s linear infinite;
}

.spinner__path {
    animation: spinner__dash 1.5s ease-in-out infinite;
    stroke: currentColor;
    stroke-dasharray: 1,200;
    stroke-dashoffset: 0;
    stroke-linecap: round;
}

@each $name, $color in $spinner--loop {
    .#{$component-name}--#{$name} {
        color: $color;
    }
}

.spinner--cycle {
    animation: spinner__cycle 6s ease-in-out infinite;
}

.switch {
    border: 0;
    font-size: 2rem;
    outline: 0;
    padding: 0;
    position: relative;
    user-select: none;
}


.switch__label {
    background: get-color('brand', 'negative');
    border-radius: $switch-dimension;
    color: transparent;
    cursor: pointer;
    display: block;
    height: $switch-dimension;
    margin-bottom: 1em;
    position: relative;
    text-indent: 100%;
    transition: background-color 150ms, margin-left 150ms;
    width: ($switch-dimension * 2);

    &::after {
        background: #fff;
        border-radius: $switch-dimension;
        bottom: 1px;
        box-shadow: 0 2px 5px rgba(0, 0, 0, .3);
        content: '';
        display: block;
        left: 1px;
        margin-left: 0;
        position: absolute;
        top: 1px;
        transform: translate3d(0, 0, 0);
        transition: background-color 150ms, margin-left 150ms;
        width: $switch-dimension;
    }
}


.switch__toggle {
    opacity: 0;
    padding: 0;
    position: absolute;

    &:checked + .switch__label {
        background: get-color('brand', 'positive');

        &::after {
            margin-left: $switch-dimension;
        }
    }
}



// BLOCK MODIFIERS \\
// =============== \\
.switch--inside {
    .switch__label {
        &::after {
            height: ($switch-dimension * .8);
            left:   ($switch-dimension * .1);
            top:    ($switch-dimension * .1);
            width:  ($switch-dimension * .8);
        }
    }

    .switch__toggle {
        &:checked + .switch__label {
            background: get-color('brand', 'positive');
        }
    }
}


.switch--outline {
    z-index: 1;

    .switch__label {
        background-color: #fff;

        &::before {
            background-color: get-color('brand', 'negative');
            border-radius: $switch-dimension;
            bottom: -.075em;
            content: '';
            display: block;
            left:   -.075em;
            position: absolute;
            right:  -.075em;
            top:    -.075em;
            z-index: -1;
        }

        &::after {
            background-color: get-color('brand', 'negative');
            box-shadow: none;
            height: ($switch-dimension * .8);
            left:   ($switch-dimension * .1);
            top:    ($switch-dimension * .1);
            width:  ($switch-dimension * .8);
        }
    }

    .switch__toggle {
        &:checked + .switch__label {
            background: #fff;
            border-color: get-color('brand', 'positive');

            // Is ok to nest this deep for psuedo elements
            // scss-lint:disable NestingDepth
            &::before,
            &::after {
                background-color: get-color('brand', 'positive');
            }
            // scss-lint:enable NestingDepth
        }
    }
}


.switch--small {
    font-size: 1.25rem;
}

.switch--large {
    font-size: 2.75rem;
}

@import 'components/plugin/accordion';
@import 'components/plugin/carousel';
@import 'components/plugin/modal';
@import 'components/plugin/tabs';
@import 'components/plugin/tooltip';

.#{$component-name} {
    background-color: #fff;
    box-shadow: 0 0 3px rgba(#000, .25);
}


/**
    [1.1] - We want the Header to look and feel clickable, even if it's not
            an anchor tag.
    [1.2] - Because Icons are a font, they usually get the text-select cursor
            when they're hovered. Force them to be "clickable"!
    [1.3] - Let's set a value for our rotate, so that the CSS has a value to
            transition to/from.
    [1.4] - When the Accordion Header is collapsed (the plugin will place a
            class on it for us), rotate the chevron icons so that they point
            in the appropriate direction.
            NOTE: This is restrictive, and forces us to target specific icon
                  classes. :(
*/
.#{$component-name}__header {
    background-color: $accordion__header-background;
    background-image: linear-gradient(to bottom, tint($accordion__header-background, 15), $accordion__header-background);
    border-top: 1px solid tint($accordion__header-background, 35);
    color: #fff;
    cursor: pointer; // [1.1]
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#{tint($accordion__header-background, 15)}', endColorstr='#{$accordion__header-background}', GradientType=0 );
    font-weight: bold;
    line-height: 2;
    padding-left:  .5em;
    padding-right: .5em;
    text-shadow: 1px 1px 0 rgba(#000, .25);
    user-select: none;

    .icon {
        cursor: pointer;             // [1.2]
        font-size: .85em;
        margin-right: .25em;
        transform: rotate(0deg);     // [1.3]
        transition: transform 250ms; // [1.3]
    }

    &.is-collapsed {
        .icon--chevron-right {
            transform: rotate(90deg);  // [1.4]
        }

        .icon--chevron-down {
            transform: rotate(-90deg); // [1.4]
        }
    }
}


/**
    [2.1] - The collapse plugin will apply a static height value when the
            element expands and collapses. All we need to do is make CSS
            transition magic!
    [2.2] - If we don't apply overflow: hidden; to the element while it's in
            the process of expanding/collapsing, then its contents will display
            above it as it's transitioning. Uuuuuuugly.
    [2.3] - The plugin won't set a 0 height to collpsed panels on page
            load, so we'll do it here!
*/
.#{$component-name}__body {
    transition: height 250ms; // [2.1]

    &.is-collapsing {
        overflow: hidden;     // [2.2]
    }

    &.can-collapse:not(.is-open) {
        height: 0;            // [2.3]
        overflow: hidden;
    }
}


.#{$component-name}__content {
    padding: .25em .5em;
}

/**
    [3.1] - We don't want the Primary Color gradient appearing on nested
            Accordion Headers, so let's remove the gradient and apply the flat
            color for nested Accordion Headers.
*/
.#{$component-name}__content .#{$component-name} {
    box-shadow: none;
    margin: -.25em -.5em;
}

.#{$component-name}__content .#{$component-name}__header {
    background-color: $accordion__header-nested-background; // [3.1]
    background-image: none;                                 // [3.1]
    border-bottom:    1px solid darken($accordion__header-nested-background, 10);
    border-top-color: tint($accordion__header-nested-background, 25);
    color: #000;
    filter: progid:DXImageTransform.Microsoft.gradient(enabled = false);
    text-shadow: none;
}

.#{$component-name}__content .#{$component-name}__content .#{$component-name}__header {
    background-color: $accordion__header-double-nested-background;
    border-bottom:    1px solid darken($accordion__header-double-nested-background, 10);
    border-top-color: tint($accordion__header-double-nested-background, 25);
}

.#{$component-name}--flat {

    .#{$component-name}__header {
        background-image: none;
        border-top: 1px solid rgba(#000, .25);
    }

    .#{$component-name}__content .#{$component-name}__header {
        border-bottom: 0;
        border-top-color: rgba(#000, .1);
    }

}

/**
    [1.1] - We set relative position here so we can precisely manipulate any
            child .carousel__content elements.
    [1.2] - Let's also strip any margins from paragraphs in our items so we
            have more reliable spacing.
*/
.#{$component-name}__item {
    position: relative; // [1.1]

    p {
        margin: 0;      // [1.2]
    }
}


/**
    [2.1] - We'll use a lage negative bottom value to "hide" the content below
            the slide. We'll then set the value to 0 when the slide is active
            and use CSS transitions to animate it.
    [2.2] - We delay the CSS transition here by 400ms. This allows the old slide
            time to disappear, and the new slide time to appear.
*/
.#{$component-name}__content {
    background-color: rgba(#000, .75);
    bottom: -100%;                  // [2.1]
    color: #fff;
    padding: .35em .75em;
    position: absolute;             // [2.1]
    text-shadow: 1px 1px 0 rgba(#000, .25);
    transition: bottom 450ms 400ms; // [2.2]
    width: 100%;

    .slick-active & {
        bottom: 0;                  // [2.1]
    }
}


/**
    [3.1] - Only the "slide image" should be 100% width of the slide, which is
            why we bind that to a class here.
*/
.#{$component-name}__image {
    width: 100%; // [3.1]
}

/**
    [4.1] - Flexbox, baby! This allows us to have multiple items display inline
            in our carousel preview, but also have them all be equal width.
            There are some downfalls to this, and mobile is not supported yet,
            but it's a good option at the moment.
    [4.2] - Our carousels have 30px margin-bottom on them. If the carousel preview
            immediately follows a carousel window, let's pull it up 30px.
*/
.#{$component-name}__preview {
    display: flex; // [4.1]

    .#{$component-name}__window + & {
        margin-top: -30px; // [4.2]
    }
}


/**
    [5.1]   - Using the flex value that we set in [4.1], we can now create a
              list of items that all have equal width, and scale accordingly!
    [5.2]   - This is our small little thumbnail! We're not going to restrict
              its dimensions with CSS. If the dev implementing them wants to
              use an image greater than 45×45, that's their problem.
    [5.2.1] - We will, however, undo the width: 100%; we set above.
*/
.#{$component-name}__preview-item {
    background: $carousel__preview-item-background;
    background-image: linear-gradient(to bottom, darken($carousel__preview-item-background, 10), $carousel__preview-item-background);
    border: 1px solid darken($carousel__preview-item-background, 15);
    box-shadow: inset 0 1px 0 rgba(#fff, .25);
    color: #fff;
    flex: 1;              // [5.1]
    font-size: .85rem;
    padding: .5em .25em;
    transition: all 300ms;

    &:not(.is-active) {
        cursor: pointer;
    }

    &.is-active {
        background-color: #fff;
        background-image: none;
        color: #333;
    }

    + .#{$component-name}__preview-item {
        border-left: 0;
    }

    p {
        margin: 0;
    }

    .#{$component-name}__title {
        margin-bottom: 0;
    }

    // [5.2]
    .#{$component-name}__image {
        border: 2px solid #fff;
        float: left;
        margin-right: 5px;
        width: auto; // [5.2.1]
    }
}

/**
    [1.1] - We want to prevent the user from scrolling when a modal is open.
*/
// We only want this rule to apply if it's on the body
// scss-lint:disable QualifyingElement
body.modal-open {
    overflow-y: hidden; // [1.1]
}
// scss-lint:enable QualifyingElement


/**
    [2.1] - We want the entire modal component to fill the full size of the viewport
            which will make it easy peasy for us to centre the actual modal dialog.
    [2.2] - We'll hide the entire modal component by default and, using the JS
            plugin, we'll apply display: block; to it when it's triggered.
*/
.#{$component-name} {
    @include z-index(modal, 1);
    bottom: 0;       // [2.1]
    display: none;   // [2.2]
    left: 0;         // [2.1]
    outline: 0 none;
    overflow: hidden;
    position: fixed; // [2.1]
    right: 0;        // [2.1]
    top: 0;          // [2.1]
}


/**
    The .modal__backdrop element is created when a modal is triggered, and it is
    injected just before </body>
        [3.1] - We want it to fill 100% of the viewport and to scroll with the user.
*/
.#{$component-name}__backdrop {
    @include z-index(modal);
    background-color: rgba(#000, .65);
    bottom: 0;       // [3.1]
    left: 0;         // [3.1]
    opacity: 0;
    position: fixed; // [3.1]
    right: 0;        // [3.1]
    top: 0;          // [3.1]
    transition: opacity 150ms linear;

    &.is-visible {
        opacity: 1;
    }
}


/**
    [4.1] - We'll force the top left corner of the dialog to appear at the centre
            of the screen, and then use CSS transform to pull it back 50% of its
            width - making it truly centred on the screen at all times.
    [4.2] - We want the modal's dialog to be flexible, and to not look broken.
            By allowing it to be 80% screen width, we can make it appear well on
            mobile devices, but restrict it to being a maximum of 600px wide to
            prevent it stretching on desktop.
*/
.#{$component-name}__dialog {
    background-color: #fff;
    box-shadow: 0 0 10px rgba(#000, .15);
    left: 50%;                         // [4.1]
    max-width: $modal-max-width;       // [4.2]
    opacity: 0;
    position: absolute;                // [4.1]
    top: 50%;                          // [4.1]
    transform: translate(-50%, -100%); // [4.1]
    transition: opacity 150ms linear, transform 150ms linear;
    width: 80%;                        // [4.2]

    .is-visible & {
        opacity: 1;
        transform: translate(-50%, -50%); // [4.1]
    }
}


/**
    [5.1] - We need to apply a clearfix here because both the .modal__title and
            .modal__close elements (the two elements in the header) are floated,
            causing the header to collapse.
    [5.2] - We apply 3em padding-right here to force long titles to break onto
            the next line, and then use negative margin in [6.1] to pull the
            Modal Close element into the padding so that any text up until the
            break appears alongside the Modal Close.
*/
.#{$component-name}__header {
    @include background--stipe($modal__header-background, 40px 40px, 5%, true);
    @include clearfix;            // [5.1]
    color: #fff;
    font-size: 14px;
    font-weight: bold;
    line-height: 1.75;
    padding: .5em 3em .5em 1em; // [5.2]
    text-shadow: 1px 1px 0 rgba(#000, .25);
}


.#{$component-name}__title {
    float: left;
    margin: 0;
}


/**
    [6.1] - We use this negative margin to pull the Modal Close button into the
            large padding-right we set in [5.2].
*/
.#{$component-name}__close {
    background: linear-gradient(to bottom, #f40000 0%, #c00 100%);
    border: 1px solid darken(#c00, 10);
    border-radius: 3px;
    box-shadow: inset 0 1px 0 rgba(#fff, .35);
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#f40000', endColorstr='#c00', GradientType=0 );
    float: right;
    height: 2em;
    margin-right: -2.5em; // [6.1]
    width: 2em;
}


.#{$component-name}__body {
    padding: 1.5rem 1rem;
}


.#{$component-name}__footer {
    border-top: 1px solid get-color('brand', 'grey-light');
    padding: 1rem;
}

.#{$component-name} {
    margin-bottom: 1rem;
}


/**
    [1.1] - All tabs should be displayed inline, so let's extend the silent class.
*/
.#{$component-name}__list {
    @include list--inline; // [1.1]
}


/**
    [2.1] - Remove the bottom border to prevent the hiddeous double-width border
            appearing below Tab Items. Later on, we'll pull the content pane
            up one pixel to hide the border beneath the tabs.
    [2.2] - We remove the left margin from the first List Item to allow the
            tabs to sit flush with the left of the content panel.
    [2.3] - Make the links appear as inline-block to allow us better control
            over their box model.
*/
.#{$component-name}__item {
    background-image: linear-gradient(to bottom, #51a339, #3c782a);
    border: 1px solid #51a339;  // [2.1]
    border-bottom: 0;        // [2.1]
    border-radius: 3px 3px 0 0;
    box-shadow: inset 0 1px 0 rgba(#fff, .5);
    color: #fff;
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#51a339', endColorstr='#3c782a', GradientType=0 );
    margin: 0 .25rem;
    text-transform: uppercase;

    &:first-child {
        margin-left: 0; // [2.2]
    }

    &.is-active {
        background-color: #fff;
        background-image: none;
        border: 1px solid get-color('brand', 'grey-light');
        border-bottom: 0;
        box-shadow: none;
        color: #000;
        filter: progid:DXImageTransform.Microsoft.gradient( enabled = false );
    }

    a {
        color: currentColor;
        display: inline-block; // [2.3]
        padding: .35em .75em;
        text-decoration: none;
    }
}


/**
    [3.1] - Pull the content pane upwards 1 pixel to hide its top border under
            each of the Tabs Items.
*/
.#{$component-name}__content {
    background-color: #fff;
    border: 1px solid get-color('brand', 'grey-light');
    color: #000;
    margin-top: -1px;
    padding: 1rem;
}


/**
    [4.1] - We'll hide the panes by default, and only show them when the modifier
            classes (.is-active, .is-visible) are applied. The application of
            these classes are staggered in the JS, allowing us to use CSS
            animation to make them appear.
*/
.#{$component-name}__pane {
    display: none;                  // [4.1]
    opacity: 0;                     // [4.1]
    transform: translateY(-10px);   // [4.1]
    transition: opacity 150ms linear, transform 150ms linear;     // [4.1]

    &.is-active {
        display: block;             // [4.1]
    }

    &.is-visible {
        opacity: 1;                 // [4.1]
        transform: translateY(0);   // [4.1]
    }
}


// FILTER TABS VARIANT \\
// =================== \\

/**
    [5.1] - CSS Arrow. This psuedo element will appear as a small chevron beneath
            the element it's attached to. We use margin-top as a way of animating
            the chevron's position, and we use opacity for some tricksy hiding and
            showing of the chevron.
*/
.#{$component-name}--filter {
    .#{$component-name}__list {
        margin-bottom: .75rem;
    }

    .#{$component-name}__item {
        background-image: linear-gradient(to bottom, #adb2b9, #8c929e);
        border: 0;
        border-radius: 3px;
        filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#adb2b9', endColorstr='#8c929e', GradientType=0 );
        font-size: .85rem;
        position: relative;
        text-shadow: 1px 1px 0 rgba(#000, .25);
        z-index: 1;

        &::after {
            border: solid transparent;
            border-color: rgba(136, 183, 213, 0);
            border-top-color: #70767e;
            border-width: 5px;
            content: ' ';
            height: 0;
            left: 50%;
            margin-left: -5px;
            margin-top: -5px;         // [5.1]
            opacity: 0;               // [5.1]
            pointer-events: none;
            position: absolute;
            top: 100%;
            transition: 150ms linear; // [5.1]
            width: 0;
            z-index: -1;
        }

        &.is-active {
            background-image: linear-gradient(to bottom, #444a54, #70767e);
            color: #fff;
            filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#444a54', endColorstr='#70767e', GradientType=0 );

            // Is ok to nest this deep for pseudo element
            // scss-lint:disable NestingDepth
            &::after {
                margin-top: 0; // [5.1]
                opacity: 1;    // [5.1]
            }
            // scss-lint:enable NestingDepth
        }
    }
}


// SWITCH TABS VARIANT \\
// =================== \\

/**
    [6.1] - We define `color: #hex;` on the `.tab--switch` modifier class.
            While this may seem strange, the decision was made as it allows
            us to then use the `currentColor` keyword throughout the modifier.
            This gives us a vastly more powerful color cascade and allows for
            "theming" of the `.tab--switch` modifier by requiring that developers
            only need to overwrite this value with their desired color:
            E.g.: `.tab--switch {color: #E06EA9;}` would result in a bingo-themed
            tab component with minimal overrides required.
*/
.#{$component-name}--switch {
    color: #004833;

    .#{$component-name}__list {
        border: 1px solid currentColor;
        border-radius: 3px;
        color: currentColor;
        display: inline-block;
        margin-bottom: .75rem;
    }

    .#{$component-name}__item {
        background-color: transparent;
        background-image: none;
        border: 0;
        border-radius: 0;
        box-shadow: none;
        color: currentColor;
        margin: 0;

        &:not(:last-child) {
            border-right: 1px solid;
        }

        a {
            color: currentColor;
        }

        &.is-active {
            background-color: currentColor;
            color: currentColor;

            // We need to come up with a better solution for this nesting depth
            // scss-lint:disable NestingDepth
            a {
                color: #fff;
            }
            // scss-lint:enable NestingDepth
        }
    }

    .#{$component-name}__content {
        border: 0;
    }
}


// FLEXBOX TABS \\
// ============ \\
.#{$component-name}--flex {
    .#{$component-name}__list {
        display: flex;
        overflow-x: auto;
    }

    .#{$component-name}__item {
        flex: 1 1 0;

        &:last-child {
            margin-right: 0;
        }

        a {
            width: 100%;
        }
    }
}

/**
    [1.1] - We'll set an absolute position here on the base class, and then
            apply the actual positions with modifying classes (tooltip--left)
    [1.2] - Set our properties so that our CSS transitions have values to
            transition to/from.
*/
.#{$component-name} {
    @include z-index(tooltip);
    color: $tooltip-color;
    display: block;
    font-size: .85rem;
    font-weight: normal;
    line-height: 1.2;
    opacity: 0;
    position: absolute; // [1.1]
    transition: opacity 100ms linear, transform 100ms linear;

    &.is-visible {
        opacity: 1;               // [1.2]
        transform: translateY(0); // [1.2]
    }
}


.#{$component-name}__inner {
    background-color: $tooltip-background;
    box-shadow: 0 0 5px rgba(#000, .15);
    display: inline-block;
    max-width: $tooltip-max-width;
    padding: .35em .65em;
}


/**
    The Tooltip Arrow is an empty element used to create exactly what you think
    it does.
        [2.1] - We set its position to absolute, and will position it according
                to the tooltip's modifying class.
*/
.#{$component-name}__arrow {
    border-color: transparent;
    border-style: solid;
    height: 0;
    position: absolute; // [2.1]
    width: 0;
}

.#{$component-name}--top {
    padding: $tooltip__arrow-size 0;
    transform: translateY(5px);

    .#{$component-name}__arrow {
        border-top-color: $tooltip-background;
        border-width: $tooltip__arrow-size $tooltip__arrow-size 0;
        bottom: 0;
        left: 50%;
        margin-left: -$tooltip__arrow-size;
    }
}


.#{$component-name}--right {
    padding: 0 $tooltip__arrow-size;
    transform: translateX(-5px);

    .#{$component-name}__arrow {
        border-right-color: $tooltip-background;
        border-width: $tooltip__arrow-size $tooltip__arrow-size $tooltip__arrow-size 0;
        left: 0;
        margin-top: -$tooltip__arrow-size;
        top: 50%;
    }
}


.#{$component-name}--bottom {
    padding: $tooltip__arrow-size 0;
    transform: translateY(-5px);

    .#{$component-name}__arrow {
        border-bottom-color: $tooltip-background;
        border-width: 0 $tooltip__arrow-size $tooltip__arrow-size;
        left: 50%;
        margin-left: -$tooltip__arrow-size;
        top: 0;
    }
}


.#{$component-name}--left {
    padding: 0 $tooltip__arrow-size;
    transform: translateX(5px);

    .#{$component-name}__arrow {
        border-left-color: $tooltip-background;
        border-width: $tooltip__arrow-size 0 $tooltip__arrow-size $tooltip__arrow-size;
        margin-top: -$tooltip__arrow-size;
        right: 0;
        top: 50%;
    }
}

section.css:
@import 'helpers/classes';
@import 'base/skeleton';
@import 'base/tables';
@import 'base/forms';

@import 'components/typography/headings';
@import 'components/typography/lists';


// ========================================================================= \\

h1,
h2,
h3,
h4,
h5,
h6 {
    letter-spacing: -.005em;
    margin: .25em 0 .45em;
    padding: 0;
}

// scss-lint:disable SingleLinePerSelector
h1, .h1 { font-size: $heading--h1; }
h2, .h2 { font-size: $heading--h2; }
h3, .h3 { font-size: $heading--h3; }
h4, .h4 { font-size: $heading--h4; }
h5, .h5 { font-size: $heading--h5; }
h6, .h6 { font-size: $heading--h6; }
// scss-lint:enable SingleLinePerSelector

@import 'foundation-sites/scss/util/util';
@import 'foundation-sites/scss/global';
@import 'foundation-sites/scss/components/visibility';

$grid-row-width:     $grid--max-width;
$grid-column-count:  $grid--columns;
$grid-column-gutter: $grid--gutter;
$breakpoints: (
    'small':   map-get($grid--breakpoints, 'small'),
    'medium':  map-get($grid--breakpoints, 'medium'),
    'large':   map-get($grid--breakpoints, 'large'),
    'xlarge':  map-get($grid--breakpoints, 'xlarge'),
    'xxlarge': map-get($grid--breakpoints, 'xxlarge')
);
$breakpoint-classes: $grid--classes;

@import 'foundation-sites/scss/grid/grid';

@if ($grid--flexbox) {
    @include foundation-flex-grid;
}
@else {
    @include foundation-grid;
}

@include foundation-visibility-classes;


// ========================================================================= \\

.table {
    background-color: $table--background;
    margin-bottom: 1rem;
    max-width: 100%;
    width: 100%;

    th {
        text-align: left;
    }

    // Cells
    > thead,
    > tbody,
    > tfoot {
        > tr {
            > th,
            > td {
                border-top: 1px solid $table--border-color;
                line-height: $line-height--base;
                padding: $table--cellpadding;
                vertical-align: top;
            }
        }
    }

    // Bottom align for column headings
    > thead > tr > th {
        border-bottom: 2px solid $table--border-color;
        vertical-align: bottom;
    }

    // Remove top border from thead by default
    > caption + thead,
    > colgroup + thead,
    > thead:first-child {
        > tr:first-child {
            > th,
            > td {
                border-top: 0;
            }
        }
    }

    // Account for multiple tbody instances
    > tbody + tbody {
        border-top: 2px solid $table--border-color;
    }
}


.table--striped {
    > tbody > tr:nth-of-type(2n+1) {
        background-color: $table--stripe;
    }
}


.table--hover {
    > tbody {
        > tr:hover {
            background-color: $table--hover;
        }
    }
}


.table--bordered {
    border: 1px solid $table--border-color;

    > thead,
    > tbody,
    > tfoot {
        > tr {
            > th,
            > td {
                border: 1px solid $table--border-color;
            }
        }
    }

    > thead > tr {
        > th,
        > td {
            border-bottom-width: 2px;
        }
    }
}

fieldset {
    margin-bottom: 1rem;
}

label {
    cursor: pointer;
    display: inline-block;
    font-weight: bold;
    margin-bottom: .2em;
    max-width: 100%;
}

legend {
    font-style: italic;
    padding: 0 .75em;
}

input {
    &[type="file"] {
        display: block;
    }

    &[type="search"] {
        box-sizing: border-box;
    }

    &[type="file"]:focus,
    &[type="checkbox"]:focus,
    &[type="radio"]:focus {
        outline: thin dotted;
        outline-offset: -2px;
    }
}

textarea {
    max-width: 100%;
}



.#{$component-name}__group {
    margin-bottom: 1rem;
}


.#{$component-name}__control {
    background-image: none;
    border: 1px solid $form__control-border;
    border-radius: 3px;
    box-shadow: inset 1px 1px 3px rgba(#000, .075);
    display: block;
    padding: .45em .65em;
    // For ease of reading.
    // scss-lint:disable SpaceAfterPropertyColon
    transition:
        border-color 250ms ease-in-out,
        box-shadow 250ms ease-in-out;
    // scss-lint:enable SpaceAfterPropertyColon
    width: 100%;

    &:focus {
        // For ease of reading
        // scss-lint:disable SpaceAfterPropertyColon
        box-shadow:
            inset 0 1px 1px rgba(#000, .075),
            0 0 3px rgba($form--aura, .35);
        // scss-lint:enable SpaceAfterPropertyColon
    }

    &:not(input):not(select):not(textarea) {
        border: 0;
        box-shadow: none;
    }

    label > & {
        margin-top: .25em;
    }
}


.#{$component-name}__message {
    background: #fff;
    border: 1px solid;
    border-radius: 3px;
    color: $form__message-color;
    display: block;
    font-size: .85rem;
    // font-weight: bold;
    margin-top: 1em;
    padding: .25em;
    position: relative;

    &::after,
    &::before {
        border: solid transparent;
        bottom: 100%;
        content: ' ';
        height: 0;
        left: 1rem;
        pointer-events: none;
        position: absolute;
        width: 0;
    }

    &::after {
        border-bottom-color: #fff;
        border-width: .8em;
        margin-left:  0;
    }

    &::before {
        border-bottom-color: inherit;
        border-width: .9em;
        margin-left:  -.1em;
    }
}


.#{$component-name}__radio,
.#{$component-name}__checkbox {
    font-weight: normal;
    padding-left: 20px;
    position: relative;

    // We only want to target radio/checkbox input
    // scss-lint:disable QualifyingElement
    input[type="radio"],
    input[type="checkbox"] {
        left: 0;
        position: absolute;
        top: .25em;
    }
    // scss-lint:enable QualifyingElement

    & + & {
        margin-left: 1rem;
    }
}


// CONTEXTUAL \\
// ========== \\
.#{$component-name} {
    .is-valid {
        &.#{$component-name}__group,
        &.#{$component-name}__message,
        &.#{$component-name}__radio,
        &.#{$component-name}__checkbox,
        .#{$component-name}__group,
        .#{$component-name}__message,
        .#{$component-name}__radio,
        .#{$component-name}__checkbox {
            color: $form--success;
        }

        &.#{$component-name}__control,
        .#{$component-name}__control {
            border-color: $form--success;
            box-shadow: none;
        }
    }

    .is-invalid {
        &.#{$component-name}__group,
        &.#{$component-name}__message,
        &.#{$component-name}__radio,
        &.#{$component-name}__checkbox,
        .#{$component-name}__group,
        .#{$component-name}__message,
        .#{$component-name}__radio,
        .#{$component-name}__checkbox {
            color: $form--error;
        }

        &.#{$component-name}__control,
        .#{$component-name}__control {
            border-color: $form--error;
            box-shadow: none;
        }
    }
}


// DISABLED STATE \\
// ============== \\
// We need to be a bit specific with the elements we target for [disabled]
// scss-lint:disable QualifyingElement
.#{$component-name}__control[disabled],
fieldset[disabled] .#{$component-name}__control {
    cursor: not-allowed;
}
// scss-lint:enable QualifyingElement


// MEDIA QUERIES \\
// ============= \\
@include breakpoint('medium') {
    .#{$component-name}--inline {
        .#{$component-name}__group {
            display: inline-block;
            margin-bottom: 0;
            vertical-align: middle;
        }

        .#{$component-name}__control {
            display: inline-block;
            vertical-align: middle;
            width: auto;
        }

        .#{$component-name}__checkbox {
            display: inline-block;
        }
    }
}

@import 'components/breadcrumbs';
@import 'components/buttons';
@import 'components/channelnav';
@import 'components/flash'; // undocumented
@import 'components/gem';
@import 'components/globalnav';
@import 'components/icons';
@import 'components/logo';
@import 'components/masthead';
@import 'components/panels';
@import 'components/pip';
@import 'components/spinner';
@import 'components/switch';


// ========================================================================= \\

/**
    [1.1] - Breadcrumbs are always inline, that's just how they roll.
    [1.2] - We let users choose their own "leading" text with the data-before=""
            attribute. If it doesn't exist, the text doesn't show.
    [1.3] - The standard 1rem font-size is a bit large for breadcrumbs, so
            let's shrink it a little.
    [1.4] - This is the divider between each of the breadcrumb items. Having
            it as an ::after pseudo-element cuts down on superfluous markup.
    [1.5] - Let's do the spacing based on the font-size of the breadcrumb items.
*/
.#{$component-name} {
    @include list--inline;          // [1.1]

    &::before {
        content: attr(data-before); // [1.2]
        font-size: .85rem;          // [1.3]
        margin-right: .25rem;
    }

    > .#{$component-name}__item {
        font-size: .85rem;          // [1.3]

        > a {
            color: get-color('brand', 'primary');
            font-weight: bold;
        }

        &:not(:last-child) {
            // Is ok to nest this deep for psuedo elements
            // scss-lint:disable NestingDepth
            &::after {
                content: '\00BB';   // [1.4]
                margin: 0 .35em;    // [1.5]
            }
            // scss-lint:enable NestingDepth
        }
    }
}

@each $name, $color in $breadcrumb--loop {

    .#{$component-name}--#{$name} {

        > .#{$component-name}__item a {
            color: $color;
        }

    }

}

/**
    Standard button styles
        [1.1] - We'll remove the border from any button element, most notable is
                when the class is applied to an *actual* <button> tag.
        [1.2] - Buttons should appear clickable regardless of whether they're a
                click element (<a>), that's kinda the point.
        [1.3] - Make buttons inline-block so we can properly manipulate their
                box-model.
        [1.4] - In order to try meet UX guideline minimum heights in a reasonable
                fashion, we set our vertical padding as a calculation of our base
                font-size (assumed 14px). This is how we calculate the padding:
                ( (desiredHeight - fontSize) ÷ 2 ) ÷ fontSize
        [1.5] - Prevent the User from being able to select text in the button.
        [1.6] - Better alignment for buttons with differing sizes when used
                beside each other.
        [1.7] - Stop text in buttons breaking on to a newline halfway through.
        [1.8] - The disabled button shouldn't show any visual feedback when it's
                pressed. Otherwise, it wouldn't really be disabled.
*/
.#{$component-name} {
    background-color: $btn--primary-bg;
    border: 0;              // [1.1]
    border-radius: $btn--border-radius;
    box-shadow: $btn--box-shadow;
    color: $btn--primary-text;
    cursor: pointer;        // [1.2]
    display: inline-block;  // [1.3]
    font-size: 1rem;        // [1.4]
    font-weight: bold;
    line-height: 1;         // [1.4]
    margin: $btn--margin;
    padding: .785em 1em;   // [1.4]
    text-align: center;
    user-select: none;      // [1.5]
    vertical-align: middle; // [1.6]
    white-space: nowrap;    // [1.7]

    @media only screen and (min-resolution: 2dppx) {
        border-radius: $btn--border-radius_2x;
        box-shadow: $btn--box-shadow_2x;
    }

    @media only screen and (min-resolution: 3dppx) {
        border-radius: $btn--border-radius_3x;
        box-shadow: $btn--box-shadow_3x;
    }

    &:focus,
    &:hover {
        color: $btn--primary-text;
        text-decoration: none;
    }

    &:active {
        &:not(.btn--disabled) { // [1.8]
            background-image: linear-gradient(to bottom, $btn--pressed-overlay 0%, $btn--pressed-overlay 100%);
            box-shadow: none;
            color: rgba(#000, .2);
            text-decoration: none;
        }
    }

    &:focus {
        outline: 1px dotted #000;
    }
}

.#{$component-name}--secondary {
    background-color: design-token('color', 'brand-blue-dark');
}

.#{$component-name}--tertiary {
    background-color: design-token('color', 'brand-grey');
}

.#{$component-name}--disabled {
    background-color: $btn--disabled-bg;
    color: $btn--disabled-text;
    cursor: not-allowed;

    &:hover {
        color: $btn--disabled-text;
    }

    &:active {
        pointer-events: none;
    }
}

[class*="#{$component-name}--odds"] {
    min-width: 3.5em;
}

.#{$component-name}--odds {
    background-color: $btn--odds-bg;
    color: $btn--odds-text;
    padding-left: 0;
    padding-right: 0;

    &:hover {
        color: $btn--odds-text;
    }
}

.#{$component-name}--odds-short,
.#{$component-name}--odds-up {
    background-color: $btn--odds-short-bg;
    color: $btn--odds-short-text;
}

.#{$component-name}--odds-drift,
