.switch {
    border: 0;
    font-size: 2rem;
    outline: 0;
    padding: 0;
    position: relative;
    user-select: none;
}


.switch__label {
    background: get-color('brand', 'negative');
    border-radius: $switch-dimension;
    color: transparent;
    cursor: pointer;
    display: block;
    height: $switch-dimension;
    margin-bottom: 1em;
    position: relative;
    text-indent: 100%;
    transition: background-color 150ms, margin-left 150ms;
    width: ($switch-dimension * 2);

    &::after {
        background: #fff;
        border-radius: $switch-dimension;
        bottom: 1px;
        box-shadow: 0 2px 5px rgba(0, 0, 0, .3);
        content: '';
        display: block;
        left: 1px;
        margin-left: 0;
        position: absolute;
        top: 1px;
        transform: translate3d(0, 0, 0);
        transition: background-color 150ms, margin-left 150ms;
        width: $switch-dimension;
    }
}


.switch__toggle {
    opacity: 0;
    padding: 0;
    position: absolute;

    &:checked + .switch__label {
        background: get-color('brand', 'positive');

        &::after {
            margin-left: $switch-dimension;
        }
    }
}



// BLOCK MODIFIERS \\
// =============== \\
.switch--inside {
    .switch__label {
        &::after {
            height: ($switch-dimension * .8);
            left:   ($switch-dimension * .1);
            top:    ($switch-dimension * .1);
            width:  ($switch-dimension * .8);
        }
    }

    .switch__toggle {
        &:checked + .switch__label {
            background: get-color('brand', 'positive');
        }
    }
}


.switch--outline {
    z-index: 1;

    .switch__label {
        background-color: #fff;

        &::before {
            background-color: get-color('brand', 'negative');
            border-radius: $switch-dimension;
            bottom: -.075em;
            content: '';
            display: block;
            left:   -.075em;
            position: absolute;
            right:  -.075em;
            top:    -.075em;
            z-index: -1;
        }

        &::after {
            background-color: get-color('brand', 'negative');
            box-shadow: none;
            height: ($switch-dimension * .8);
            left:   ($switch-dimension * .1);
            top:    ($switch-dimension * .1);
            width:  ($switch-dimension * .8);
        }
    }

    .switch__toggle {
        &:checked + .switch__label {
            background: #fff;
            border-color: get-color('brand', 'positive');

            // Is ok to nest this deep for psuedo elements
            // scss-lint:disable NestingDepth
            &::before,
            &::after {
                background-color: get-color('brand', 'positive');
            }
            // scss-lint:enable NestingDepth
        }
    }
}


.switch--small {
    font-size: 1.25rem;
}

.switch--large {
    font-size: 2.75rem;
}

@import 'components/plugin/accordion';
@import 'components/plugin/carousel';
@import 'components/plugin/modal';
@import 'components/plugin/tabs';
@import 'components/plugin/tooltip';

.#{$component-name} {
    background-color: #fff;
    box-shadow: 0 0 3px rgba(#000, .25);
}


/**
    [1.1] - We want the Header to look and feel clickable, even if it's not
            an anchor tag.
    [1.2] - Because Icons are a font, they usually get the text-select cursor
            when they're hovered. Force them to be "clickable"!
    [1.3] - Let's set a value for our rotate, so that the CSS has a value to
            transition to/from.
    [1.4] - When the Accordion Header is collapsed (the plugin will place a
            class on it for us), rotate the chevron icons so that they point
            in the appropriate direction.
            NOTE: This is restrictive, and forces us to target specific icon
                  classes. :(
*/
.#{$component-name}__header {
    background-color: $accordion__header-background;
    background-image: linear-gradient(to bottom, tint($accordion__header-background, 15), $accordion__header-background);
    border-top: 1px solid tint($accordion__header-background, 35);
    color: #fff;
    cursor: pointer; // [1.1]
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#{tint($accordion__header-background, 15)}', endColorstr='#{$accordion__header-background}', GradientType=0 );
    font-weight: bold;
    line-height: 2;
    padding-left:  .5em;
    padding-right: .5em;
    text-shadow: 1px 1px 0 rgba(#000, .25);
    user-select: none;

    .icon {
        cursor: pointer;             // [1.2]
        font-size: .85em;
        margin-right: .25em;
        transform: rotate(0deg);     // [1.3]
        transition: transform 250ms; // [1.3]
    }

    &.is-collapsed {
        .icon--chevron-right {
            transform: rotate(90deg);  // [1.4]
        }

        .icon--chevron-down {
            transform: rotate(-90deg); // [1.4]
        }
    }
}


/**
    [2.1] - The collapse plugin will apply a static height value when the
            element expands and collapses. All we need to do is make CSS
            transition magic!
    [2.2] - If we don't apply overflow: hidden; to the element while it's in
            the process of expanding/collapsing, then its contents will display
            above it as it's transitioning. Uuuuuuugly.
    [2.3] - The plugin won't set a 0 height to collpsed panels on page
            load, so we'll do it here!
*/
.#{$component-name}__body {
    transition: height 250ms; // [2.1]

    &.is-collapsing {
        overflow: hidden;     // [2.2]
    }

    &.can-collapse:not(.is-open) {
        height: 0;            // [2.3]
        overflow: hidden;
    }
}


.#{$component-name}__content {
    padding: .25em .5em;
}

/**
    [3.1] - We don't want the Primary Color gradient appearing on nested
            Accordion Headers, so let's remove the gradient and apply the flat
            color for nested Accordion Headers.
*/
.#{$component-name}__content .#{$component-name} {
    box-shadow: none;
    margin: -.25em -.5em;
}

.#{$component-name}__content .#{$component-name}__header {
    background-color: $accordion__header-nested-background; // [3.1]
    background-image: none;                                 // [3.1]
    border-bottom:    1px solid darken($accordion__header-nested-background, 10);
    border-top-color: tint($accordion__header-nested-background, 25);
    color: #000;
    filter: progid:DXImageTransform.Microsoft.gradient(enabled = false);
    text-shadow: none;
}

.#{$component-name}__content .#{$component-name}__content .#{$component-name}__header {
    background-color: $accordion__header-double-nested-background;
    border-bottom:    1px solid darken($accordion__header-double-nested-background, 10);
    border-top-color: tint($accordion__header-double-nested-background, 25);
}

.#{$component-name}--flat {

    .#{$component-name}__header {
        background-image: none;
        border-top: 1px solid rgba(#000, .25);
    }

    .#{$component-name}__content .#{$component-name}__header {
        border-bottom: 0;
        border-top-color: rgba(#000, .1);
    }

}

/**
    [1.1] - We set relative position here so we can precisely manipulate any
            child .carousel__content elements.
    [1.2] - Let's also strip any margins from paragraphs in our items so we
            have more reliable spacing.
*/
.#{$component-name}__item {
    position: relative; // [1.1]

    p {
        margin: 0;      // [1.2]
    }
}


/**
    [2.1] - We'll use a lage negative bottom value to "hide" the content below
            the slide. We'll then set the value to 0 when the slide is active
            and use CSS transitions to animate it.
    [2.2] - We delay the CSS transition here by 400ms. This allows the old slide
            time to disappear, and the new slide time to appear.
*/
.#{$component-name}__content {
    background-color: rgba(#000, .75);
    bottom: -100%;                  // [2.1]
    color: #fff;
    padding: .35em .75em;
    position: absolute;             // [2.1]
    text-shadow: 1px 1px 0 rgba(#000, .25);
    transition: bottom 450ms 400ms; // [2.2]
    width: 100%;

    .slick-active & {
        bottom: 0;                  // [2.1]
    }
}


/**
    [3.1] - Only the "slide image" should be 100% width of the slide, which is
            why we bind that to a class here.
*/
.#{$component-name}__image {
    width: 100%; // [3.1]
}

/**
    [4.1] - Flexbox, baby! This allows us to have multiple items display inline
            in our carousel preview, but also have them all be equal width.
            There are some downfalls to this, and mobile is not supported yet,
            but it's a good option at the moment.
    [4.2] - Our carousels have 30px margin-bottom on them. If the carousel preview
            immediately follows a carousel window, let's pull it up 30px.
*/
.#{$component-name}__preview {
    display: flex; // [4.1]

    .#{$component-name}__window + & {
        margin-top: -30px; // [4.2]
    }
}


/**
    [5.1]   - Using the flex value that we set in [4.1], we can now create a
              list of items that all have equal width, and scale accordingly!
    [5.2]   - This is our small little thumbnail! We're not going to restrict
              its dimensions with CSS. If the dev implementing them wants to
              use an image greater than 45×45, that's their problem.
    [5.2.1] - We will, however, undo the width: 100%; we set above.
*/
.#{$component-name}__preview-item {
    background: $carousel__preview-item-background;
    background-image: linear-gradient(to bottom, darken($carousel__preview-item-background, 10), $carousel__preview-item-background);
    border: 1px solid darken($carousel__preview-item-background, 15);
    box-shadow: inset 0 1px 0 rgba(#fff, .25);
    color: #fff;
    flex: 1;              // [5.1]
    font-size: .85rem;
    padding: .5em .25em;
    transition: all 300ms;

    &:not(.is-active) {
        cursor: pointer;
    }

    &.is-active {
        background-color: #fff;
        background-image: none;
        color: #333;
    }

    + .#{$component-name}__preview-item {
        border-left: 0;
    }

    p {
        margin: 0;
    }

    .#{$component-name}__title {
        margin-bottom: 0;
    }

    // [5.2]
    .#{$component-name}__image {
        border: 2px solid #fff;
        float: left;
        margin-right: 5px;
        width: auto; // [5.2.1]
    }
}

/**
    [1.1] - We want to prevent the user from scrolling when a modal is open.
*/
// We only want this rule to apply if it's on the body
// scss-lint:disable QualifyingElement
body.modal-open {
    overflow-y: hidden; // [1.1]
}
// scss-lint:enable QualifyingElement


/**
    [2.1] - We want the entire modal component to fill the full size of the viewport
            which will make it easy peasy for us to centre the actual modal dialog.
    [2.2] - We'll hide the entire modal component by default and, using the JS
            plugin, we'll apply display: block; to it when it's triggered.
*/
.#{$component-name} {
    @include z-index(modal, 1);
    bottom: 0;       // [2.1]
    display: none;   // [2.2]
    left: 0;         // [2.1]
    outline: 0 none;
    overflow: hidden;
    position: fixed; // [2.1]
    right: 0;        // [2.1]
    top: 0;          // [2.1]
}


/**
    The .modal__backdrop element is created when a modal is triggered, and it is
    injected just before </body>
        [3.1] - We want it to fill 100% of the viewport and to scroll with the user.
*/
.#{$component-name}__backdrop {
    @include z-index(modal);
    background-color: rgba(#000, .65);
    bottom: 0;       // [3.1]
    left: 0;         // [3.1]
    opacity: 0;
    position: fixed; // [3.1]
    right: 0;        // [3.1]
    top: 0;          // [3.1]
    transition: opacity 150ms linear;

    &.is-visible {
        opacity: 1;
    }
}


/**
    [4.1] - We'll force the top left corner of the dialog to appear at the centre
            of the screen, and then use CSS transform to pull it back 50% of its
            width - making it truly centred on the screen at all times.
    [4.2] - We want the modal's dialog to be flexible, and to not look broken.
            By allowing it to be 80% screen width, we can make it appear well on
            mobile devices, but restrict it to being a maximum of 600px wide to
            prevent it stretching on desktop.
*/
.#{$component-name}__dialog {
    background-color: #fff;
    box-shadow: 0 0 10px rgba(#000, .15);
    left: 50%;                         // [4.1]
    max-width: $modal-max-width;       // [4.2]
    opacity: 0;
    position: absolute;                // [4.1]
    top: 50%;                          // [4.1]
    transform: translate(-50%, -100%); // [4.1]
    transition: opacity 150ms linear, transform 150ms linear;
    width: 80%;                        // [4.2]

    .is-visible & {
        opacity: 1;
        transform: translate(-50%, -50%); // [4.1]
    }
}


/**
    [5.1] - We need to apply a clearfix here because both the .modal__title and
            .modal__close elements (the two elements in the header) are floated,
            causing the header to collapse.
    [5.2] - We apply 3em padding-right here to force long titles to break onto
            the next line, and then use negative margin in [6.1] to pull the
            Modal Close element into the padding so that any text up until the
            break appears alongside the Modal Close.
*/
.#{$component-name}__header {
    @include background--stipe($modal__header-background, 40px 40px, 5%, true);
    @include clearfix;            // [5.1]
    color: #fff;
    font-size: 14px;
    font-weight: bold;
    line-height: 1.75;
    padding: .5em 3em .5em 1em; // [5.2]
    text-shadow: 1px 1px 0 rgba(#000, .25);
}


.#{$component-name}__title {
    float: left;
    margin: 0;
}


/**
    [6.1] - We use this negative margin to pull the Modal Close button into the
            large padding-right we set in [5.2].
*/
.#{$component-name}__close {
    background: linear-gradient(to bottom, #f40000 0%, #c00 100%);
    border: 1px solid darken(#c00, 10);
    border-radius: 3px;
    box-shadow: inset 0 1px 0 rgba(#fff, .35);
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#f40000', endColorstr='#c00', GradientType=0 );
    float: right;
    height: 2em;
    margin-right: -2.5em; // [6.1]
    width: 2em;
}


.#{$component-name}__body {
    padding: 1.5rem 1rem;
}


.#{$component-name}__footer {
    border-top: 1px solid get-color('brand', 'grey-light');
    padding: 1rem;
}

.#{$component-name} {
    margin-bottom: 1rem;
}


/**
    [1.1] - All tabs should be displayed inline, so let's extend the silent class.
*/
.#{$component-name}__list {
    @include list--inline; // [1.1]
}


/**
    [2.1] - Remove the bottom border to prevent the hiddeous double-width border
            appearing below Tab Items. Later on, we'll pull the content pane
            up one pixel to hide the border beneath the tabs.
    [2.2] - We remove the left margin from the first List Item to allow the
            tabs to sit flush with the left of the content panel.
    [2.3] - Make the links appear as inline-block to allow us better control
            over their box model.
*/
.#{$component-name}__item {
    background-image: linear-gradient(to bottom, #51a339, #3c782a);
    border: 1px solid #51a339;  // [2.1]
    border-bottom: 0;        // [2.1]
    border-radius: 3px 3px 0 0;
    box-shadow: inset 0 1px 0 rgba(#fff, .5);
    color: #fff;
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#51a339', endColorstr='#3c782a', GradientType=0 );
    margin: 0 .25rem;
    text-transform: uppercase;

    &:first-child {
        margin-left: 0; // [2.2]
    }

    &.is-active {
        background-color: #fff;
        background-image: none;
        border: 1px solid get-color('brand', 'grey-light');
        border-bottom: 0;
        box-shadow: none;
        color: #000;
        filter: progid:DXImageTransform.Microsoft.gradient( enabled = false );
    }

    a {
        color: currentColor;
        display: inline-block; // [2.3]
        padding: .35em .75em;
        text-decoration: none;
    }
}


/**
    [3.1] - Pull the content pane upwards 1 pixel to hide its top border under
            each of the Tabs Items.
*/
.#{$component-name}__content {
    background-color: #fff;
    border: 1px solid get-color('brand', 'grey-light');
    color: #000;
    margin-top: -1px;
    padding: 1rem;
}


/**
    [4.1] - We'll hide the panes by default, and only show them when the modifier
            classes (.is-active, .is-visible) are applied. The application of
            these classes are staggered in the JS, allowing us to use CSS
            animation to make them appear.
*/
.#{$component-name}__pane {
    display: none;                  // [4.1]
    opacity: 0;                     // [4.1]
    transform: translateY(-10px);   // [4.1]
    transition: opacity 150ms linear, transform 150ms linear;     // [4.1]

    &.is-active {
        display: block;             // [4.1]
    }

    &.is-visible {
        opacity: 1;                 // [4.1]
        transform: translateY(0);   // [4.1]
    }
}


// FILTER TABS VARIANT \\
// =================== \\

/**
    [5.1] - CSS Arrow. This psuedo element will appear as a small chevron beneath
            the element it's attached to. We use margin-top as a way of animating
            the chevron's position, and we use opacity for some tricksy hiding and
            showing of the chevron.
*/
.#{$component-name}--filter {
    .#{$component-name}__list {
        margin-bottom: .75rem;
    }

    .#{$component-name}__item {
        background-image: linear-gradient(to bottom, #adb2b9, #8c929e);
        border: 0;
        border-radius: 3px;
        filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#adb2b9', endColorstr='#8c929e', GradientType=0 );
        font-size: .85rem;
        position: relative;
        text-shadow: 1px 1px 0 rgba(#000, .25);
        z-index: 1;

        &::after {
            border: solid transparent;
            border-color: rgba(136, 183, 213, 0);
            border-top-color: #70767e;
            border-width: 5px;
            content: ' ';
            height: 0;
            left: 50%;
            margin-left: -5px;
            margin-top: -5px;         // [5.1]
            opacity: 0;               // [5.1]
            pointer-events: none;
            position: absolute;
            top: 100%;
            transition: 150ms linear; // [5.1]
            width: 0;
            z-index: -1;
        }

        &.is-active {
            background-image: linear-gradient(to bottom, #444a54, #70767e);
            color: #fff;
            filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#444a54', endColorstr='#70767e', GradientType=0 );

            // Is ok to nest this deep for pseudo element
            // scss-lint:disable NestingDepth
            &::after {
                margin-top: 0; // [5.1]
                opacity: 1;    // [5.1]
            }
            // scss-lint:enable NestingDepth
        }
    }
}


// SWITCH TABS VARIANT \\
// =================== \\

/**
    [6.1] - We define `color: #hex;` on the `.tab--switch` modifier class.
            While this may seem strange, the decision was made as it allows
            us to then use the `currentColor` keyword throughout the modifier.
            This gives us a vastly more powerful color cascade and allows for
            "theming" of the `.tab--switch` modifier by requiring that developers
            only need to overwrite this value with their desired color:
            E.g.: `.tab--switch {color: #E06EA9;}` would result in a bingo-themed
            tab component with minimal overrides required.
*/
.#{$component-name}--switch {
    color: #004833;

    .#{$component-name}__list {
        border: 1px solid currentColor;
        border-radius: 3px;
        color: currentColor;
        display: inline-block;
        margin-bottom: .75rem;
    }

    .#{$component-name}__item {
        background-color: transparent;
        background-image: none;
        border: 0;
        border-radius: 0;
        box-shadow: none;
        color: currentColor;
        margin: 0;

        &:not(:last-child) {
            border-right: 1px solid;
        }

        a {
            color: currentColor;
        }

        &.is-active {
            background-color: currentColor;
            color: currentColor;

            // We need to come up with a better solution for this nesting depth
            // scss-lint:disable NestingDepth
            a {
                color: #fff;
            }
            // scss-lint:enable NestingDepth
        }
    }

    .#{$component-name}__content {
        border: 0;
    }
}


// FLEXBOX TABS \\
// ============ \\
.#{$component-name}--flex {
    .#{$component-name}__list {
        display: flex;
        overflow-x: auto;
    }

    .#{$component-name}__item {
        flex: 1 1 0;

        &:last-child {
            margin-right: 0;
        }

        a {
            width: 100%;
        }
    }
}

/**
    [1.1] - We'll set an absolute position here on the base class, and then
            apply the actual positions with modifying classes (tooltip--left)
    [1.2] - Set our properties so that our CSS transitions have values to
            transition to/from.
*/
.#{$component-name} {
    @include z-index(tooltip);
    color: $tooltip-color;
    display: block;
    font-size: .85rem;
    font-weight: normal;
    line-height: 1.2;
    opacity: 0;
    position: absolute; // [1.1]
    transition: opacity 100ms linear, transform 100ms linear;

    &.is-visible {
        opacity: 1;               // [1.2]
        transform: translateY(0); // [1.2]
    }
}


.#{$component-name}__inner {
    background-color: $tooltip-background;
    box-shadow: 0 0 5px rgba(#000, .15);
    display: inline-block;
    max-width: $tooltip-max-width;
    padding: .35em .65em;
}


/**
    The Tooltip Arrow is an empty element used to create exactly what you think
    it does.
        [2.1] - We set its position to absolute, and will position it according
                to the tooltip's modifying class.
*/
.#{$component-name}__arrow {
    border-color: transparent;
    border-style: solid;
    height: 0;
    position: absolute; // [2.1]
    width: 0;
}

.#{$component-name}--top {
    padding: $tooltip__arrow-size 0;
    transform: translateY(5px);

    .#{$component-name}__arrow {
        border-top-color: $tooltip-background;
        border-width: $tooltip__arrow-size $tooltip__arrow-size 0;
        bottom: 0;
        left: 50%;
        margin-left: -$tooltip__arrow-size;
    }
}


.#{$component-name}--right {
    padding: 0 $tooltip__arrow-size;
    transform: translateX(-5px);

    .#{$component-name}__arrow {
        border-right-color: $tooltip-background;
        border-width: $tooltip__arrow-size $tooltip__arrow-size $tooltip__arrow-size 0;
        left: 0;
        margin-top: -$tooltip__arrow-size;
        top: 50%;
    }
}


.#{$component-name}--bottom {
    padding: $tooltip__arrow-size 0;
    transform: translateY(-5px);

    .#{$component-name}__arrow {
        border-bottom-color: $tooltip-background;
        border-width: 0 $tooltip__arrow-size $tooltip__arrow-size;
        left: 50%;
        margin-left: -$tooltip__arrow-size;
        top: 0;
    }
}


.#{$component-name}--left {
    padding: 0 $tooltip__arrow-size;
    transform: translateX(5px);

    .#{$component-name}__arrow {
        border-left-color: $tooltip-background;
        border-width: $tooltip__arrow-size 0 $tooltip__arrow-size $tooltip__arrow-size;
        margin-top: -$tooltip__arrow-size;
        right: 0;
        top: 50%;
    }
}
